import React, { useState, useEffect } from 'react';
import {
  Box,
  Typography,
  TextField,
  Button,
  Card,
  CardContent,
  CardMedia,
  Grid,
  Paper,
  CircularProgress,
  FormControl,
  Stack,
  Checkbox,
  FormGroup,
  FormControlLabel,
  Stepper,
  Step,
  StepLabel,
  StepContent,
  ToggleButton,
  ToggleButtonGroup,
  Chip,
} from '@mui/material';
import { DatePicker } from '@mui/x-date-pickers/DatePicker';
import { LocalizationProvider } from '@mui/x-date-pickers/LocalizationProvider';
import { AdapterDateFns } from '@mui/x-date-pickers/AdapterDateFns';
import SendIcon from '@mui/icons-material/Send';
import CalendarMonthIcon from '@mui/icons-material/CalendarMonth';
import RepeatIcon from '@mui/icons-material/Repeat';
import { addDays, format } from 'date-fns';

// Mock data for meal suggestions (replace with actual API calls later)
const mockMeals = [
  {
    id: 1,
    name: 'Grilled Chicken Salad',
    image: 'https://source.unsplash.com/random/400x300/?salad',
    calories: 350,
    time: '25 mins',
  },
  {
    id: 2,
    name: 'Salmon with Quinoa',
    image: 'https://source.unsplash.com/random/400x300/?salmon',
    calories: 450,
    time: '30 mins',
  },
  {
    id: 3,
    name: 'Vegetarian Stir Fry',
    image: 'https://source.unsplash.com/random/400x300/?stirfry',
    calories: 300,
    time: '20 mins',
  },
];

const thinkingPhrases = [
  "Analyzing your preferences...",
  "Exploring delicious options...",
  "Crafting your perfect meal plan...",
  "Considering nutritional balance...",
  "Finding the best recipes for you...",
];

const mealTypes = [
  { id: 'breakfast', label: 'Breakfast' },
  { id: 'lunch', label: 'Lunch' },
  { id: 'dinner', label: 'Dinner' },
  { id: 'snack', label: 'Snacks' },
];

const weekDays = [
  { id: 'monday', label: 'Mon' },
  { id: 'tuesday', label: 'Tue' },
  { id: 'wednesday', label: 'Wed' },
  { id: 'thursday', label: 'Thu' },
  { id: 'friday', label: 'Fri' },
  { id: 'saturday', label: 'Sat' },
  { id: 'sunday', label: 'Sun' },
];

function MealPlanner() {
  const [activeStep, setActiveStep] = useState(0);
  const [prompt, setPrompt] = useState('');
  const [loading, setLoading] = useState(false);
  const [suggestions, setSuggestions] = useState([]);
  const [selectedMeals, setSelectedMeals] = useState({
    breakfast: false,
    lunch: false,
    dinner: false,
    snack: false,
  });
  const [planType, setPlanType] = useState('date-range');
  const [startDate, setStartDate] = useState(new Date());
  const [endDate, setEndDate] = useState(addDays(new Date(), 7));
  const [selectedDays, setSelectedDays] = useState([]);
  const [thinkingText, setThinkingText] = useState('');
  const [currentPhraseIndex, setCurrentPhraseIndex] = useState(0);
  const [currentCharIndex, setCurrentCharIndex] = useState(0);

  const handlePlanTypeChange = (event, newType) => {
    if (newType !== null) {
      setPlanType(newType);
    }
  };

  const handleDayToggle = (day) => {
    setSelectedDays(prev => 
      prev.includes(day)
        ? prev.filter(d => d !== day)
        : [...prev, day]
    );
  };

  const handleMealTypeChange = (event) => {
    setSelectedMeals({
      ...selectedMeals,
      [event.target.name]: event.target.checked,
    });
  };

  const getSelectedMealTypes = () => {
    return Object.entries(selectedMeals)
      .filter(([_, isSelected]) => isSelected)
      .map(([type]) => type);
  };

  const handleNext = () => {
    setActiveStep((prevStep) => prevStep + 1);
  };

  const handleBack = () => {
    setActiveStep((prevStep) => prevStep - 1);
  };

  useEffect(() => {
    if (loading) {
      const phraseInterval = setInterval(() => {
        setCurrentPhraseIndex((prev) => (prev + 1) % thinkingPhrases.length);
        setCurrentCharIndex(0);
      }, 2000);

      const charInterval = setInterval(() => {
        setCurrentCharIndex((prev) => {
          const currentPhrase = thinkingPhrases[currentPhraseIndex];
          if (prev < currentPhrase.length) {
            setThinkingText(currentPhrase.substring(0, prev + 1));
            return prev + 1;
          }
          return prev;
        });
      }, 50);

      return () => {
        clearInterval(phraseInterval);
        clearInterval(charInterval);
      };
    } else {
      setThinkingText('');
      setCurrentPhraseIndex(0);
      setCurrentCharIndex(0);
    }
  }, [loading, currentPhraseIndex]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);

    // Simulate API call
    setTimeout(() => {
      setSuggestions(mockMeals);
      setLoading(false);
    }, 4000);
  };

  const steps = [
    {
      label: 'Choose When to Plan',
      content: (
        <Stack spacing={3}>
          <ToggleButtonGroup
            value={planType}
            exclusive
            onChange={handlePlanTypeChange}
            fullWidth
            sx={{ mb: 2 }}
          >
            <ToggleButton value="date-range" aria-label="date range">
              <CalendarMonthIcon sx={{ mr: 1 }} />
              Specific Dates
            </ToggleButton>
            <ToggleButton value="recurring" aria-label="recurring">
              <RepeatIcon sx={{ mr: 1 }} />
              Weekly Schedule
            </ToggleButton>
          </ToggleButtonGroup>

          {planType === 'date-range' ? (
            <LocalizationProvider dateAdapter={AdapterDateFns}>
              <Stack spacing={2}>
                <DatePicker
                  label="Start Date"
                  value={startDate}
                  onChange={(newValue) => {
                    setStartDate(newValue);
                    setEndDate(addDays(newValue, 7));
                  }}
                  disablePast
                  slotProps={{
                    textField: { fullWidth: true },
                  }}
                />
                <DatePicker
                  label="End Date"
                  value={endDate}
                  onChange={(newValue) => setEndDate(newValue)}
                  disablePast
                  minDate={startDate}
                  slotProps={{
                    textField: { fullWidth: true },
                  }}
                />
              </Stack>
            </LocalizationProvider>
          ) : (
            <Box>
              <Typography variant="subtitle1" gutterBottom>
                Select days for your weekly meal plan:
              </Typography>
              <Box 
                sx={{ 
                  display: 'flex', 
                  gap: 1, 
                  flexWrap: 'wrap',
                  justifyContent: 'center'
                }}
              >
                {weekDays.map((day) => (
                  <Chip
                    key={day.id}
                    label={day.label}
                    onClick={() => handleDayToggle(day.id)}
                    color="primary"
                    variant={selectedDays.includes(day.id) ? "filled" : "outlined"}
                    sx={{ 
                      minWidth: '70px',
                      fontSize: '1rem',
                    }}
                  />
                ))}
              </Box>
            </Box>
          )}
        </Stack>
      ),
    },
    {
      label: 'Select Meal Types',
      content: (
        <FormControl 
          component="fieldset" 
          sx={{ 
            width: '100%',
            p: 2, 
            border: '1px solid',
            borderColor: 'divider',
            borderRadius: 1
          }}
        >
          <Grid container spacing={2}>
            {mealTypes.map((type) => (
              <Grid item xs={6} sm={3} key={type.id}>
                <FormControlLabel
                  control={
                    <Checkbox
                      checked={selectedMeals[type.id]}
                      onChange={handleMealTypeChange}
                      name={type.id}
                      color="primary"
                    />
                  }
                  label={type.label}
                />
              </Grid>
            ))}
          </Grid>
        </FormControl>
      ),
    },
    {
      label: 'Describe Your Preferences',
      content: (
        <TextField
          fullWidth
          variant="outlined"
          placeholder="E.g., 'I want healthy vegetarian meals' or 'Quick protein-rich recipes'"
          value={prompt}
          onChange={(e) => setPrompt(e.target.value)}
          multiline
          rows={3}
        />
      ),
    },
  ];

  const isStepValid = (step) => {
    switch (step) {
      case 0:
        return planType === 'date-range' 
          ? (startDate && endDate && endDate > startDate)
          : selectedDays.length > 0;
      case 1:
        return Object.values(selectedMeals).some(Boolean);
      case 2:
        return prompt.trim().length > 0;
      default:
        return true;
    }
  };

  return (
    <Box sx={{ py: 4 }}>
      <Typography
        variant="h1"
        sx={{
          mb: 3,
          textAlign: 'center',
          fontSize: { xs: '2.5rem', md: '3.5rem' },
        }}
      >
        Meal planning made easy
      </Typography>

      <Typography
        variant="h6"
        color="text.secondary"
        sx={{ mb: 4, textAlign: 'center' }}
      >
        Tell us what you're looking for, and we'll create a personalized meal plan
      </Typography>

      <Paper
        component="form"
        onSubmit={handleSubmit}
        elevation={2}
        sx={{
          p: 3,
          maxWidth: 800,
          mx: 'auto',
          mb: 6,
          borderRadius: 2,
        }}
      >
        <Stepper activeStep={activeStep} orientation="vertical">
          {steps.map((step, index) => (
            <Step key={step.label}>
              <StepLabel>{step.label}</StepLabel>
              <StepContent>
                <Box sx={{ mb: 2 }}>
                  {step.content}
                  <Box sx={{ mt: 2, display: 'flex', gap: 1 }}>
                    <Button
                      variant="contained"
                      onClick={index === steps.length - 1 ? handleSubmit : handleNext}
                      disabled={loading || !isStepValid(index)}
                    >
                      {index === steps.length - 1 ? 'Generate Plan' : 'Continue'}
                    </Button>
                    {index > 0 && (
                      <Button onClick={handleBack}>
                        Back
                      </Button>
                    )}
                  </Box>
                </Box>
              </StepContent>
            </Step>
          ))}
        </Stepper>
      </Paper>

      {loading && (
        <Box
          sx={{
            textAlign: 'center',
            mb: 4,
            minHeight: '3em',
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center',
          }}
        >
          <Typography
            variant="h6"
            color="primary"
            sx={{
              fontWeight: 500,
              opacity: 0.9,
              animation: 'pulse 1.5s infinite',
              '@keyframes pulse': {
                '0%': { opacity: 0.6 },
                '50%': { opacity: 1 },
                '100%': { opacity: 0.6 },
              },
            }}
          >
            {thinkingText}
          </Typography>
        </Box>
      )}

      {suggestions.length > 0 && (
        <Box>
          <Typography variant="h5" sx={{ mb: 3 }}>
            Your Meal Plan
          </Typography>
          <Box sx={{ mb: 2 }}>
            <Typography variant="subtitle1" color="text.secondary">
              {planType === 'date-range' 
                ? `${format(startDate, 'MMM d, yyyy')} - ${format(endDate, 'MMM d, yyyy')}`
                : `Weekly plan for: ${selectedDays.map(day => 
                    day.charAt(0).toUpperCase() + day.slice(1)
                  ).join(', ')}`
              }
            </Typography>
            <Typography variant="subtitle1" color="text.secondary">
              Selected meal types: {getSelectedMealTypes().map(type => 
                type.charAt(0).toUpperCase() + type.slice(1)
              ).join(', ')}
            </Typography>
          </Box>
          <Grid container spacing={3}>
            {suggestions.map((meal) => (
              <Grid item xs={12} sm={6} md={4} key={meal.id}>
                <Card 
                  sx={{ 
                    height: '100%',
                    transition: 'transform 0.2s',
                    '&:hover': {
                      transform: 'translateY(-4px)',
                      boxShadow: 4,
                    },
                  }}
                >
                  <CardMedia
                    component="img"
                    height="200"
                    image={meal.image}
                    alt={meal.name}
                  />
                  <CardContent>
                    <Typography variant="h6" gutterBottom>
                      {meal.name}
                    </Typography>
                    <Typography variant="body2" color="text.secondary">
                      {meal.calories} calories • {meal.time}
                    </Typography>
                  </CardContent>
                </Card>
              </Grid>
            ))}
          </Grid>
        </Box>
      )}
    </Box>
  );
}

export default MealPlanner;
