import React, { useState } from 'react';
import {
  Box,
  Typography,
  TextField,
  Button,
  Paper,
  Grid,
  Chip,
  Stack,
  MenuItem,
  FormControl,
  InputLabel,
  Select,
  OutlinedInput,
  Alert,
} from '@mui/material';
import { mealCategories, dietaryTags } from '../data/meals';

const AddMealForm = ({ onSubmit, onCancel }) => {
  const [formData, setFormData] = useState({
    name: '',
    description: '',
    image: '',
    category: [],
    dietaryInfo: {
      calories: '',
      protein: '',
      carbs: '',
      fat: '',
      fiber: '',
    },
    tags: [],
    prepTime: '',
    cookTime: '',
    difficulty: 'easy',
    servings: '',
    ingredients: [{ item: '', amount: '', unit: '' }],
    instructions: [''],
  });

  const [submitted, setSubmitted] = useState(false);

  const handleChange = (e) => {
    const { name, value } = e.target;
    if (name.includes('.')) {
      const [parent, child] = name.split('.');
      setFormData(prev => ({
        ...prev,
        [parent]: {
          ...prev[parent],
          [child]: value
        }
      }));
    } else {
      setFormData(prev => ({
        ...prev,
        [name]: value
      }));
    }
  };

  const handleCategoryChange = (event) => {
    setFormData(prev => ({
      ...prev,
      category: event.target.value
    }));
  };

  const handleTagsChange = (event) => {
    setFormData(prev => ({
      ...prev,
      tags: event.target.value
    }));
  };

  const handleIngredientChange = (index, field, value) => {
    setFormData(prev => ({
      ...prev,
      ingredients: prev.ingredients.map((ing, i) => 
        i === index ? { ...ing, [field]: value } : ing
      )
    }));
  };

  const addIngredient = () => {
    setFormData(prev => ({
      ...prev,
      ingredients: [...prev.ingredients, { item: '', amount: '', unit: '' }]
    }));
  };

  const removeIngredient = (index) => {
    setFormData(prev => ({
      ...prev,
      ingredients: prev.ingredients.filter((_, i) => i !== index)
    }));
  };

  const handleInstructionChange = (index, value) => {
    setFormData(prev => ({
      ...prev,
      instructions: prev.instructions.map((inst, i) => 
        i === index ? value : inst
      )
    }));
  };

  const addInstruction = () => {
    setFormData(prev => ({
      ...prev,
      instructions: [...prev.instructions, '']
    }));
  };

  const removeInstruction = (index) => {
    setFormData(prev => ({
      ...prev,
      instructions: prev.instructions.filter((_, i) => i !== index)
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Generate a unique ID
    const newMeal = {
      ...formData,
      id: String(Date.now()),
      dietaryInfo: {
        calories: Number(formData.dietaryInfo.calories),
        protein: Number(formData.dietaryInfo.protein),
        carbs: Number(formData.dietaryInfo.carbs),
        fat: Number(formData.dietaryInfo.fat),
        fiber: Number(formData.dietaryInfo.fiber),
      },
      prepTime: Number(formData.prepTime),
      cookTime: Number(formData.cookTime),
      servings: Number(formData.servings),
    };

    onSubmit(newMeal);
    setSubmitted(true);
  };

  if (submitted) {
    return (
      <Box sx={{ textAlign: 'center', py: 4 }}>
        <Alert severity="success" sx={{ mb: 2 }}>
          Thank you for contributing! Your meal has been added successfully.
        </Alert>
        <Button variant="contained" onClick={() => {
          setSubmitted(false);
          setFormData({
            name: '',
            description: '',
            image: '',
            category: [],
            dietaryInfo: {
              calories: '',
              protein: '',
              carbs: '',
              fat: '',
              fiber: '',
            },
            tags: [],
            prepTime: '',
            cookTime: '',
            difficulty: 'easy',
            servings: '',
            ingredients: [{ item: '', amount: '', unit: '' }],
            instructions: [''],
          });
        }}>
          Add Another Meal
        </Button>
        <Button sx={{ ml: 2 }} onClick={onCancel}>
          Return to Meal Planner
        </Button>
      </Box>
    );
  }

  return (
    <Paper component="form" onSubmit={handleSubmit} sx={{ p: 3, maxWidth: 800, mx: 'auto' }}>
      <Typography variant="h4" gutterBottom>
        Add Your Recipe
      </Typography>
      <Typography variant="body1" color="text.secondary" paragraph>
        Share your favorite recipe with the MealBuddy community! Please provide as much detail as possible to help others recreate your dish.
      </Typography>

      <Grid container spacing={3}>
        <Grid item xs={12}>
          <TextField
            required
            fullWidth
            label="Recipe Name"
            name="name"
            value={formData.name}
            onChange={handleChange}
          />
        </Grid>

        <Grid item xs={12}>
          <TextField
            required
            fullWidth
            multiline
            rows={3}
            label="Description"
            name="description"
            value={formData.description}
            onChange={handleChange}
          />
        </Grid>

        <Grid item xs={12}>
          <TextField
            required
            fullWidth
            label="Image URL"
            name="image"
            value={formData.image}
            onChange={handleChange}
            helperText="Provide a URL to an image of your dish"
          />
        </Grid>

        <Grid item xs={12} sm={6}>
          <FormControl fullWidth required>
            <InputLabel>Categories</InputLabel>
            <Select
              multiple
              value={formData.category}
              onChange={handleCategoryChange}
              input={<OutlinedInput label="Categories" />}
              renderValue={(selected) => (
                <Box sx={{ display: 'flex', flexWrap: 'wrap', gap: 0.5 }}>
                  {selected.map((value) => (
                    <Chip 
                      key={value} 
                      label={mealCategories.find(cat => cat.id === value)?.name} 
                    />
                  ))}
                </Box>
              )}
            >
              {mealCategories.map((category) => (
                <MenuItem key={category.id} value={category.id}>
                  {category.name}
                </MenuItem>
              ))}
            </Select>
          </FormControl>
        </Grid>

        <Grid item xs={12} sm={6}>
          <FormControl fullWidth required>
            <InputLabel>Tags</InputLabel>
            <Select
              multiple
              value={formData.tags}
              onChange={handleTagsChange}
              input={<OutlinedInput label="Tags" />}
              renderValue={(selected) => (
                <Box sx={{ display: 'flex', flexWrap: 'wrap', gap: 0.5 }}>
                  {selected.map((value) => (
                    <Chip 
                      key={value} 
                      label={dietaryTags.find(tag => tag.id === value)?.label} 
                    />
                  ))}
                </Box>
              )}
            >
              {dietaryTags.map((tag) => (
                <MenuItem key={tag.id} value={tag.id}>
                  {tag.label}
                </MenuItem>
              ))}
            </Select>
          </FormControl>
        </Grid>

        <Grid item xs={12}>
          <Typography variant="h6" gutterBottom>
            Nutritional Information
          </Typography>
          <Grid container spacing={2}>
            {Object.keys(formData.dietaryInfo).map((key) => (
              <Grid item xs={6} sm={4} md={2.4} key={key}>
                <TextField
                  required
                  fullWidth
                  type="number"
                  label={key.charAt(0).toUpperCase() + key.slice(1)}
                  name={`dietaryInfo.${key}`}
                  value={formData.dietaryInfo[key]}
                  onChange={handleChange}
                />
              </Grid>
            ))}
          </Grid>
        </Grid>

        <Grid item xs={12} sm={4}>
          <TextField
            required
            fullWidth
            type="number"
            label="Prep Time (minutes)"
            name="prepTime"
            value={formData.prepTime}
            onChange={handleChange}
          />
        </Grid>

        <Grid item xs={12} sm={4}>
          <TextField
            required
            fullWidth
            type="number"
            label="Cook Time (minutes)"
            name="cookTime"
            value={formData.cookTime}
            onChange={handleChange}
          />
        </Grid>

        <Grid item xs={12} sm={4}>
          <TextField
            required
            fullWidth
            type="number"
            label="Servings"
            name="servings"
            value={formData.servings}
            onChange={handleChange}
          />
        </Grid>

        <Grid item xs={12}>
          <Typography variant="h6" gutterBottom>
            Ingredients
          </Typography>
          {formData.ingredients.map((ingredient, index) => (
            <Box key={index} sx={{ mb: 2, display: 'flex', gap: 2 }}>
              <TextField
                required
                label="Ingredient"
                value={ingredient.item}
                onChange={(e) => handleIngredientChange(index, 'item', e.target.value)}
                sx={{ flexGrow: 1 }}
              />
              <TextField
                required
                type="number"
                label="Amount"
                value={ingredient.amount}
                onChange={(e) => handleIngredientChange(index, 'amount', e.target.value)}
                sx={{ width: '100px' }}
              />
              <TextField
                required
                label="Unit"
                value={ingredient.unit}
                onChange={(e) => handleIngredientChange(index, 'unit', e.target.value)}
                sx={{ width: '100px' }}
              />
              <Button 
                color="error" 
                onClick={() => removeIngredient(index)}
                disabled={formData.ingredients.length === 1}
              >
                Remove
              </Button>
            </Box>
          ))}
          <Button onClick={addIngredient}>Add Ingredient</Button>
        </Grid>

        <Grid item xs={12}>
          <Typography variant="h6" gutterBottom>
            Instructions
          </Typography>
          {formData.instructions.map((instruction, index) => (
            <Box key={index} sx={{ mb: 2, display: 'flex', gap: 2 }}>
              <TextField
                required
                fullWidth
                multiline
                label={`Step ${index + 1}`}
                value={instruction}
                onChange={(e) => handleInstructionChange(index, e.target.value)}
              />
              <Button 
                color="error" 
                onClick={() => removeInstruction(index)}
                disabled={formData.instructions.length === 1}
              >
                Remove
              </Button>
            </Box>
          ))}
          <Button onClick={addInstruction}>Add Step</Button>
        </Grid>

        <Grid item xs={12}>
          <Stack direction="row" spacing={2} justifyContent="flex-end">
            <Button onClick={onCancel}>Cancel</Button>
            <Button type="submit" variant="contained">Submit Recipe</Button>
          </Stack>
        </Grid>
      </Grid>
    </Paper>
  );
};

export default AddMealForm;
