import React, { useState, useEffect } from 'react';
import {
  Box,
  Typography,
  TextField,
  Button,
  Card,
  CardContent,
  CardMedia,
  Grid,
  Paper,
  CircularProgress,
  Select,
  MenuItem,
  FormControl,
  InputLabel,
  Stack,
} from '@mui/material';
import SendIcon from '@mui/icons-material/Send';

// Mock data for meal suggestions (replace with actual API calls later)
const mockMeals = [
  {
    id: 1,
    name: 'Grilled Chicken Salad',
    image: 'https://source.unsplash.com/random/400x300/?salad',
    calories: 350,
    time: '25 mins',
  },
  {
    id: 2,
    name: 'Salmon with Quinoa',
    image: 'https://source.unsplash.com/random/400x300/?salmon',
    calories: 450,
    time: '30 mins',
  },
  {
    id: 3,
    name: 'Vegetarian Stir Fry',
    image: 'https://source.unsplash.com/random/400x300/?stirfry',
    calories: 300,
    time: '20 mins',
  },
];

const thinkingPhrases = [
  "Analyzing your preferences...",
  "Exploring delicious options...",
  "Crafting your perfect meal plan...",
  "Considering nutritional balance...",
  "Finding the best recipes for you...",
];

function MealPlanner() {
  const [prompt, setPrompt] = useState('');
  const [loading, setLoading] = useState(false);
  const [suggestions, setSuggestions] = useState([]);
  const [mealType, setMealType] = useState('');
  const [planDuration, setPlanDuration] = useState(1);
  const [thinkingText, setThinkingText] = useState('');
  const [currentPhraseIndex, setCurrentPhraseIndex] = useState(0);
  const [currentCharIndex, setCurrentCharIndex] = useState(0);

  useEffect(() => {
    if (loading) {
      const phraseInterval = setInterval(() => {
        setCurrentPhraseIndex((prev) => (prev + 1) % thinkingPhrases.length);
        setCurrentCharIndex(0);
      }, 2000);

      const charInterval = setInterval(() => {
        setCurrentCharIndex((prev) => {
          const currentPhrase = thinkingPhrases[currentPhraseIndex];
          if (prev < currentPhrase.length) {
            setThinkingText(currentPhrase.substring(0, prev + 1));
            return prev + 1;
          }
          return prev;
        });
      }, 50);

      return () => {
        clearInterval(phraseInterval);
        clearInterval(charInterval);
      };
    } else {
      setThinkingText('');
      setCurrentPhraseIndex(0);
      setCurrentCharIndex(0);
    }
  }, [loading, currentPhraseIndex]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);

    // Simulate API call
    setTimeout(() => {
      setSuggestions(mockMeals);
      setLoading(false);
    }, 4000);
  };

  return (
    <Box sx={{ py: 4 }}>
      <Typography
        variant="h1"
        sx={{
          mb: 3,
          textAlign: 'center',
          fontSize: { xs: '2.5rem', md: '3.5rem' },
        }}
      >
        Meal planning made easy
      </Typography>

      <Typography
        variant="h6"
        color="text.secondary"
        sx={{ mb: 4, textAlign: 'center' }}
      >
        Tell us what you're looking for, and we'll create a personalized meal plan
      </Typography>

      <Paper
        component="form"
        onSubmit={handleSubmit}
        elevation={2}
        sx={{
          p: 3,
          maxWidth: 800,
          mx: 'auto',
          mb: 6,
          borderRadius: 2,
        }}
      >
        <Stack spacing={2}>
          <TextField
            fullWidth
            variant="outlined"
            placeholder="E.g., 'I want healthy vegetarian meals' or 'Quick protein-rich recipes'"
            value={prompt}
            onChange={(e) => setPrompt(e.target.value)}
          />

          <Box sx={{ display: 'flex', gap: 2 }}>
            <FormControl fullWidth>
              <InputLabel>Meal Type</InputLabel>
              <Select
                value={mealType}
                label="Meal Type"
                onChange={(e) => setMealType(e.target.value)}
              >
                <MenuItem value="breakfast">Breakfast</MenuItem>
                <MenuItem value="lunch">Lunch</MenuItem>
                <MenuItem value="dinner">Dinner</MenuItem>
                <MenuItem value="snack">Snack</MenuItem>
              </Select>
            </FormControl>

            <FormControl fullWidth>
              <InputLabel>Plan Duration</InputLabel>
              <Select
                value={planDuration}
                label="Plan Duration"
                onChange={(e) => setPlanDuration(e.target.value)}
              >
                <MenuItem value={1}>1 day</MenuItem>
                <MenuItem value={2}>2 days</MenuItem>
                <MenuItem value={3}>3 days</MenuItem>
                <MenuItem value={4}>4 days</MenuItem>
                <MenuItem value={5}>5 days</MenuItem>
                <MenuItem value={6}>6 days</MenuItem>
                <MenuItem value={7}>1 week</MenuItem>
              </Select>
            </FormControl>
          </Box>

          <Button
            variant="contained"
            color="primary"
            size="large"
            endIcon={loading ? <CircularProgress size={20} color="inherit" /> : <SendIcon />}
            type="submit"
            disabled={!prompt || loading || !mealType}
            sx={{ minWidth: 200 }}
          >
            {loading ? 'Generating...' : 'Get Meal Plan'}
          </Button>
        </Stack>
      </Paper>

      {loading && (
        <Box
          sx={{
            textAlign: 'center',
            mb: 4,
            minHeight: '3em',
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center',
          }}
        >
          <Typography
            variant="h6"
            color="primary"
            sx={{
              fontWeight: 500,
              opacity: 0.9,
              animation: 'pulse 1.5s infinite',
              '@keyframes pulse': {
                '0%': { opacity: 0.6 },
                '50%': { opacity: 1 },
                '100%': { opacity: 0.6 },
              },
            }}
          >
            {thinkingText}
          </Typography>
        </Box>
      )}

      {suggestions.length > 0 && (
        <Box>
          <Typography variant="h5" sx={{ mb: 3 }}>
            {`Your ${mealType} Plan for ${planDuration} ${planDuration === 1 ? 'day' : 'days'}`}
          </Typography>
          <Grid container spacing={3}>
            {suggestions.map((meal) => (
              <Grid item xs={12} sm={6} md={4} key={meal.id}>
                <Card 
                  sx={{ 
                    height: '100%',
                    transition: 'transform 0.2s',
                    '&:hover': {
                      transform: 'translateY(-4px)',
                      boxShadow: 4,
                    },
                  }}
                >
                  <CardMedia
                    component="img"
                    height="200"
                    image={meal.image}
                    alt={meal.name}
                  />
                  <CardContent>
                    <Typography variant="h6" gutterBottom>
                      {meal.name}
                    </Typography>
                    <Typography variant="body2" color="text.secondary">
                      {meal.calories} calories â€¢ {meal.time}
                    </Typography>
                  </CardContent>
                </Card>
              </Grid>
            ))}
          </Grid>
        </Box>
      )}
    </Box>
  );
}

export default MealPlanner;
