import React, { useState, useEffect } from 'react';
import {
  Box,
  Typography,
  TextField,
  Button,
  Card,
  CardContent,
  CardMedia,
  Grid,
  Paper,
  CircularProgress,
  Select,
  MenuItem,
  FormControl,
  InputLabel,
  Stack,
  Checkbox,
  FormGroup,
  FormControlLabel,
  Stepper,
  Step,
  StepLabel,
  StepContent,
} from '@mui/material';
import { DatePicker } from '@mui/x-date-pickers/DatePicker';
import { LocalizationProvider } from '@mui/x-date-pickers/LocalizationProvider';
import { AdapterDateFns } from '@mui/x-date-pickers/AdapterDateFns';
import SendIcon from '@mui/icons-material/Send';
import { addDays } from 'date-fns';

// Mock data for meal suggestions (replace with actual API calls later)
const mockMeals = [
  {
    id: 1,
    name: 'Grilled Chicken Salad',
    image: 'https://source.unsplash.com/random/400x300/?salad',
    calories: 350,
    time: '25 mins',
  },
  {
    id: 2,
    name: 'Salmon with Quinoa',
    image: 'https://source.unsplash.com/random/400x300/?salmon',
    calories: 450,
    time: '30 mins',
  },
  {
    id: 3,
    name: 'Vegetarian Stir Fry',
    image: 'https://source.unsplash.com/random/400x300/?stirfry',
    calories: 300,
    time: '20 mins',
  },
];

const thinkingPhrases = [
  "Analyzing your preferences...",
  "Exploring delicious options...",
  "Crafting your perfect meal plan...",
  "Considering nutritional balance...",
  "Finding the best recipes for you...",
];

const mealTypes = [
  { id: 'breakfast', label: 'Breakfast' },
  { id: 'lunch', label: 'Lunch' },
  { id: 'dinner', label: 'Dinner' },
  { id: 'snack', label: 'Snacks' },
];

function MealPlanner() {
  const [activeStep, setActiveStep] = useState(0);
  const [prompt, setPrompt] = useState('');
  const [loading, setLoading] = useState(false);
  const [suggestions, setSuggestions] = useState([]);
  const [selectedMeals, setSelectedMeals] = useState({
    breakfast: false,
    lunch: false,
    dinner: false,
    snack: false,
  });
  const [planDuration, setPlanDuration] = useState(1);
  const [startDate, setStartDate] = useState(new Date());
  const [endDate, setEndDate] = useState(addDays(new Date(), 1));
  const [thinkingText, setThinkingText] = useState('');
  const [currentPhraseIndex, setCurrentPhraseIndex] = useState(0);
  const [currentCharIndex, setCurrentCharIndex] = useState(0);

  useEffect(() => {
    // Update end date when duration changes
    setEndDate(addDays(startDate, planDuration));
  }, [planDuration, startDate]);

  const handleMealTypeChange = (event) => {
    setSelectedMeals({
      ...selectedMeals,
      [event.target.name]: event.target.checked,
    });
  };

  const getSelectedMealTypes = () => {
    return Object.entries(selectedMeals)
      .filter(([_, isSelected]) => isSelected)
      .map(([type]) => type);
  };

  const handleNext = () => {
    setActiveStep((prevStep) => prevStep + 1);
  };

  const handleBack = () => {
    setActiveStep((prevStep) => prevStep - 1);
  };

  useEffect(() => {
    if (loading) {
      const phraseInterval = setInterval(() => {
        setCurrentPhraseIndex((prev) => (prev + 1) % thinkingPhrases.length);
        setCurrentCharIndex(0);
      }, 2000);

      const charInterval = setInterval(() => {
        setCurrentCharIndex((prev) => {
          const currentPhrase = thinkingPhrases[currentPhraseIndex];
          if (prev < currentPhrase.length) {
            setThinkingText(currentPhrase.substring(0, prev + 1));
            return prev + 1;
          }
          return prev;
        });
      }, 50);

      return () => {
        clearInterval(phraseInterval);
        clearInterval(charInterval);
      };
    } else {
      setThinkingText('');
      setCurrentPhraseIndex(0);
      setCurrentCharIndex(0);
    }
  }, [loading, currentPhraseIndex]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);

    // Simulate API call
    setTimeout(() => {
      setSuggestions(mockMeals);
      setLoading(false);
    }, 4000);
  };

  const steps = [
    {
      label: 'Select Plan Duration',
      content: (
        <FormControl fullWidth>
          <InputLabel>Plan Duration</InputLabel>
          <Select
            value={planDuration}
            label="Plan Duration"
            onChange={(e) => setPlanDuration(e.target.value)}
          >
            <MenuItem value={1}>1 day</MenuItem>
            <MenuItem value={2}>2 days</MenuItem>
            <MenuItem value={3}>3 days</MenuItem>
            <MenuItem value={4}>4 days</MenuItem>
            <MenuItem value={5}>5 days</MenuItem>
            <MenuItem value={6}>6 days</MenuItem>
            <MenuItem value={7}>1 week</MenuItem>
          </Select>
        </FormControl>
      ),
    },
    {
      label: 'Choose Dates',
      content: (
        <LocalizationProvider dateAdapter={AdapterDateFns}>
          <Stack spacing={2}>
            <DatePicker
              label="Start Date"
              value={startDate}
              onChange={(newValue) => setStartDate(newValue)}
              disablePast
              slotProps={{
                textField: { fullWidth: true },
              }}
            />
            <DatePicker
              label="End Date"
              value={endDate}
              disabled
              slotProps={{
                textField: { fullWidth: true },
              }}
            />
          </Stack>
        </LocalizationProvider>
      ),
    },
    {
      label: 'Select Meal Types',
      content: (
        <FormControl 
          component="fieldset" 
          sx={{ 
            width: '100%',
            p: 2, 
            border: '1px solid',
            borderColor: 'divider',
            borderRadius: 1
          }}
        >
          <Grid container spacing={2}>
            {mealTypes.map((type) => (
              <Grid item xs={6} sm={3} key={type.id}>
                <FormControlLabel
                  control={
                    <Checkbox
                      checked={selectedMeals[type.id]}
                      onChange={handleMealTypeChange}
                      name={type.id}
                      color="primary"
                    />
                  }
                  label={type.label}
                />
              </Grid>
            ))}
          </Grid>
        </FormControl>
      ),
    },
    {
      label: 'Describe Your Preferences',
      content: (
        <TextField
          fullWidth
          variant="outlined"
          placeholder="E.g., 'I want healthy vegetarian meals' or 'Quick protein-rich recipes'"
          value={prompt}
          onChange={(e) => setPrompt(e.target.value)}
          multiline
          rows={3}
        />
      ),
    },
  ];

  return (
    <Box sx={{ py: 4 }}>
      <Typography
        variant="h1"
        sx={{
          mb: 3,
          textAlign: 'center',
          fontSize: { xs: '2.5rem', md: '3.5rem' },
        }}
      >
        Meal planning made easy
      </Typography>

      <Typography
        variant="h6"
        color="text.secondary"
        sx={{ mb: 4, textAlign: 'center' }}
      >
        Tell us what you're looking for, and we'll create a personalized meal plan
      </Typography>

      <Paper
        component="form"
        onSubmit={handleSubmit}
        elevation={2}
        sx={{
          p: 3,
          maxWidth: 800,
          mx: 'auto',
          mb: 6,
          borderRadius: 2,
        }}
      >
        <Stepper activeStep={activeStep} orientation="vertical">
          {steps.map((step, index) => (
            <Step key={step.label}>
              <StepLabel>{step.label}</StepLabel>
              <StepContent>
                <Box sx={{ mb: 2 }}>
                  {step.content}
                  <Box sx={{ mt: 2, display: 'flex', gap: 1 }}>
                    <Button
                      variant="contained"
                      onClick={index === steps.length - 1 ? handleSubmit : handleNext}
                      disabled={
                        loading ||
                        (index === 0 && !planDuration) ||
                        (index === 2 && !Object.values(selectedMeals).some(Boolean)) ||
                        (index === 3 && !prompt)
                      }
                    >
                      {index === steps.length - 1 ? 'Generate Plan' : 'Continue'}
                    </Button>
                    {index > 0 && (
                      <Button onClick={handleBack}>
                        Back
                      </Button>
                    )}
                  </Box>
                </Box>
              </StepContent>
            </Step>
          ))}
        </Stepper>
      </Paper>

      {loading && (
        <Box
          sx={{
            textAlign: 'center',
            mb: 4,
            minHeight: '3em',
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center',
          }}
        >
          <Typography
            variant="h6"
            color="primary"
            sx={{
              fontWeight: 500,
              opacity: 0.9,
              animation: 'pulse 1.5s infinite',
              '@keyframes pulse': {
                '0%': { opacity: 0.6 },
                '50%': { opacity: 1 },
                '100%': { opacity: 0.6 },
              },
            }}
          >
            {thinkingText}
          </Typography>
        </Box>
      )}

      {suggestions.length > 0 && (
        <Box>
          <Typography variant="h5" sx={{ mb: 3 }}>
            Your Meal Plan
          </Typography>
          <Box sx={{ mb: 2 }}>
            <Typography variant="subtitle1" color="text.secondary">
              {`${startDate.toLocaleDateString()} - ${endDate.toLocaleDateString()}`}
            </Typography>
            <Typography variant="subtitle1" color="text.secondary">
              Selected meal types: {getSelectedMealTypes().map(type => type.charAt(0).toUpperCase() + type.slice(1)).join(', ')}
            </Typography>
          </Box>
          <Grid container spacing={3}>
            {suggestions.map((meal) => (
              <Grid item xs={12} sm={6} md={4} key={meal.id}>
                <Card 
                  sx={{ 
                    height: '100%',
                    transition: 'transform 0.2s',
                    '&:hover': {
                      transform: 'translateY(-4px)',
                      boxShadow: 4,
                    },
                  }}
                >
                  <CardMedia
                    component="img"
                    height="200"
                    image={meal.image}
                    alt={meal.name}
                  />
                  <CardContent>
                    <Typography variant="h6" gutterBottom>
                      {meal.name}
                    </Typography>
                    <Typography variant="body2" color="text.secondary">
                      {meal.calories} calories â€¢ {meal.time}
                    </Typography>
                  </CardContent>
                </Card>
              </Grid>
            ))}
          </Grid>
        </Box>
      )}
    </Box>
  );
}

export default MealPlanner;
